import React from "react";
import { Component } from "react";
import axios from 'axios';
export class detail2 extends Component{
    state = {
        detail: [],
      };
      componentDidMount() {
        axios.get("www.themealdb.com/api/json/v1/1/lookup.php?i=")
        .then((res) => {
          console.log(res);
          this.setState({
            detail: res.data.meals,
          });
        });
      }
    render(){
        return(
            <div className="container-fluid menu">
            <div className="row">
              {this.state.detail.map((res) => (
                <div className="col-lg-3 col-md-6 mt-5">
                  <div className="card text-white bg-secondary mb-3">
                    <img className="card-img-top" src={res.strMealThumb} alt="" />
                    <div className="card-body">
                      <h4 className="card-title">{res.strTags}</h4>
                      <p className="card-text">
                          
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
          );
        
    }

}
export default detail2;
