import axios from 'axios';
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
 export class Detail extends Component {
  state = {
    obj: [],
  };
  componentWillMount = () => {
    const login = this.props.match.params.login;
    axios.get(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${login}`)
      .then((res) => {
        this.setState({
          obj: res.data.meals,
        });
      });
  };

  render() {
    return (
      <div mx-0 className="container-fluid ">
        {this.state.obj.map((getData) => (
          <div className="row">
            <div className="col-lg-6">
              <img className="card-img-top" src={getData.strMealThumb} alt="" />
            </div>
            <div className="col-lg-6">
              <h4 className="">
                {getData.strMeal}
                <Link className="m-5 for" onClick={this.props.ajouter}>
                  <i className="fas fa-heart"></i>
                </Link>
              </h4>
              <h2>Their compon ents</h2>
              <h3>
                {getData.strIngredient1} ,{getData.strIngredient2},
                {getData.strIngredient3} ,{getData.strIngredient4},
                {getData.strIngredient9} ,{getData.strIngredient5},
                {getData.strIngredient6},{getData.strIngredient7},
                {getData.strIngredient8},{getData.strIngredient10},
              </h3>
              <h2>Preparation method</h2>

              <h6> {getData.strInstructions}</h6>
            </div>
          </div>
        ))}
      </div>
    );
  }
}
export default Detail;
